package org.tsnf.operator;

public class AssignmentOperatorDemo {

	public static void main(String[] args) {
		int n1=23;
		int n2=2;
		n1-=n2;//n1=n1-n2;
		System.out.println(n1);
		n2*=n1;//n2=n2*n1
		System.out.println(n2);
		n1/=n2;
		
		System.out.println(n1);
		// TODO Auto-generated method stub

	}

}
